#include "FilteringType.h"FilteringType::FilteringType(){    this->type = none;}FilteringType::FilteringType(offer_type _type){    this->type = _type;}DynamicArray FilteringType::filter(DynamicArray& data){    DynamicArray filteredArr;    for(int i = 0; i < data.getLength(); ++i){        if(data.get(i).getType() == this->type){            filteredArr.append(data.get(i));        }    }    return filteredArr;}